(function integrateDashboardWithBrowserMetrics(){var metrics=require("internal/browser-metrics");var $=require("jquery");var DASHBOARD_PAGE_KEY="jira.dashboard";function whenIframeLoaded(iframe){var loading=$.Deferred();if(iframe.contentDocument&&iframe.contentDocument.readyState==="complete"){loading.resolve()}else{$(iframe).load(function resolveIframeLoading(){loading.resolve()})}return loading.promise()}function whenIframesLoaded(iframes){var loadPromises=iframes.map(function toIframeLoadedPromise(){return whenIframeLoaded(this)});return $.when.apply($,loadPromises)}function whenDashboardInitialized(){var initialized=$.Deferred();$(function inspectDashboard(){var dashboard=$("#dashboard");if(dashboard.length===0){initialized.reject()}if(dashboard.hasClass("initializing")){dashboard.on("initialized",function resolveDeferred(){initialized.resolve()})}else{initialized.resolve()}});return initialized.promise()}metrics.start({key:DASHBOARD_PAGE_KEY,isInitial:true});whenDashboardInitialized().pipe(function waitForIframesToLoaded(){var iframes=$("#dashboard").find("iframe").get();return whenIframesLoaded(iframes)}).done(function endMeasurement(){metrics.end({key:DASHBOARD_PAGE_KEY})})})();