define("jira/field/role-picker-util",["jquery","jira/ajs/list/group-descriptor","jira/ajs/list/item-descriptor"],function($,GroupDescriptor,ItemDescriptor){var RolePickerUtil={};RolePickerUtil.formatResponse=function(data){var ret=[];$(data).each(function(i,suggestions){var groupDescriptor=new GroupDescriptor({weight:i,label:suggestions.header});$(suggestions.roles).each(function(){groupDescriptor.addItem(new ItemDescriptor({value:this.name,label:this.name,title:this.name,html:this.html,highlighted:true}))});ret.push(groupDescriptor)});return ret};return RolePickerUtil});