AJS.test.require(["jira.webresources:jira-global","jira.webresources:jquery-livestamp"],function(){var moment=require("jira/moment");var formatter=require("jira/moment/moment.jira.formatter").translateSimpleDateFormat;module("moment.js JIRA i18n");test("date formatting",function(){var m=moment.utc("2001-07-04T12:08:56.235");equal(m.format(formatter("yyyy.MM.dd 'at' HH:mm:ss")),"2001.07.04 at 12:08:56");equal(m.format(formatter("EEE, MMM d, ''yy")),"Wed, Jul 4, '01");equal(m.format(formatter("h:mm a")),"12:08 PM");equal(m.format(formatter("hh 'o''clock' a")),"12 o'clock PM");equal(m.format(formatter("yyyy.MMMM.dd hh:mm a")),"2001.July.04 12:08 PM");equal(m.format(formatter("EEE, d MMM yyyy HH:mm:ss Z")),"Wed, 4 Jul 2001 12:08:56 +0000");equal(m.format(formatter("yyMMddHHmmssZ")),"010704120856+0000");equal(m.format(formatter("yyyy-MM-dd'T'HH:mm:ss.SSSZ")),"2001-07-04T12:08:56.235+0000");equal(m.format(formatter("yyyy-MM-dd'T'HH:mm:ss.SSSXXX")),"2001-07-04T12:08:56.235+00:00");equal(m.format(formatter("YYYY-'W'ww-u")),"2001-W27-3");equal(m.format(formatter("h:mm a")),"12:08 PM");equal(m.format(formatter("EEEE h:mm a")),"Wednesday 12:08 PM");equal(m.format(formatter("dd/MMM/yy h:mm a")),"04/Jul/01 12:08 PM");equal(m.format(formatter("dd/MMM/yy")),"04/Jul/01")})});