define("internal/util/indeterminate-checkbox-ie-fix",["internal/util/navigator","jira/skate","jquery"],function(Navigator,skate,$){function clickedOrPressedSpace(event){return event.type==="mouseup"||event.type==="click"||(event.type==="keyup"&&event.keyCode===32)}if(!Navigator.isIE()){return }skate("indeterminate-ie-fix",{type:skate.type.CLASSNAME,insert:function(element){var $element=$(element);var initialIndeterminateState;$element.on("keyup mouseup",function(event){if(clickedOrPressedSpace(event)){initialIndeterminateState=$element.is(":indeterminate")}});$element.click(function(event){if(!clickedOrPressedSpace(event)){return }if(initialIndeterminateState){$element.prop("checked",true);$element.trigger("change")}})}})});